window.onload = function () {
    clock();
    setInterval(clock, 1000);
}

function clock() {
    let hourHand = document.getElementsByClassName('hourHand');
    let minuteHand = document.getElementsByClassName('minuteHand');
    let secondHand = document.getElementsByClassName('secondHand');
    let time = document.getElementsByClassName('time');

    const today = new Date();

    let second = today.getSeconds();
    let secondDeg = ((second / 60) * 360) + 360;
    secondHand.style.transform = `rotate(${secondDeg}deg)`;

    let minute = today.getMinutes();
    let minuteDeg = ((minute / 60) * 360);
    minuteHand.style.transform = `rotate(${minuteDeg}deg)`;

    let hour = today.getHours();
    let hourDeg = ((hour / 12) * 360);
    hourHand.style.transform = `rotate(${hourDeg}deg)`;

    // time.innerHTML = '<span>' + '<strong>' + hour + '</strong>' + ' : ' + minute + ' : ' + '<small>' + second + '</small>' + '</span>';

    time.innerHTML = "second";

}


export default clock;
