window.onload = function () {

    const hourHand = document.getElementById('hourHand');
    const minuteHand = document.getElementById('minuteHand');
    const secondHand = document.getElementById('secondHand');
    const date = document.getElementById('date');

    const months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"]
    const temp = document.getElementById('test');

    function setDate() {
        const today = new Date();

        const second = today.getSeconds();
        const minute = today.getMinutes();
        const hour = today.getHours();

        let secondDeg = ((second / 60) * 360);
        let minuteDeg = ((minute / 60) * 360);
        let hourDeg = ((hour / 12) * 360);

        secondHand.style.transform = `rotate(${secondDeg}deg)`;
        minuteHand.style.transform = `rotate(${minuteDeg}deg)`;
        hourHand.style.transform = `rotate(${hourDeg}deg)`;

        switch (today.getDay()) {
            case 1:
                date.innerHTML = '<span><strong>1st </strong>' + months[today.getMonth() - 1] + today.getFullYear() + '</span>';
                break;
            case 2:
                date.innerHTML = '<span><strong>2nd </strong>' + months[today.getMonth() - 1] + today.getFullYear() + '</span>';
                break;
            case 3:
                date.innerHTML = '<span><strong>3rd </strong>' + months[today.getMonth() - 1] + today.getFullYear() + '</span>';
                break;
            default:
                date.innerHTML = '<span><strong>' + today.getDay() + 'th </strong>' + months[today.getMonth() - 1] + today.getFullYear() + '</span>';
        }

        // date.innerHTML = '<span>' + '<strong>' + today.getDay() + '</strong>' + ' : ' + today.getMonth() + ' : ' + today.getFullYear() + '</span>';

    }

    setInterval(setDate, 1000);

}
