import { useState, useEffect } from 'react';
import DateFnsUtils from '@date-io/date-fns'; // choose your lib
import {
    TimePicker,
    MuiPickersUtilsProvider,
} from '@material-ui/pickers';

import './Alarm.css'

const Alarm = () => {
    const [selectedDate, handleDateChange] = useState(new Date());
    const [alarmList, setAlarm] = useState([]);

    const click = () => {

        var alarm = {
            Time: { Hour: selectedDate.getHours(), Minute: selectedDate.getMinutes() },
            Date: "Today",
            Reminder: document.getElementById("reminder").value,
        };
        setAlarm(alarmList.push(alarm));

        console.log(alarm);
        console.log(selectedDate.getHours());
        console.log(selectedDate.getMinutes());
    }

    useEffect(() => {
        alarmList.map((person, index) => (
            console.log(person)
        ))
    }, [alarmList]);


    return (
        <>
            <div class="container">
                <nav>
                    <div>
                        <h2> Set Alarm </h2>
                    </div>
                    <div className="form">
                        <MuiPickersUtilsProvider utils={DateFnsUtils}>
                            <TimePicker value={selectedDate} onChange={handleDateChange} />
                        </MuiPickersUtilsProvider>
                        <input type="textBox" id="reminder" placeholder="Reminder Text" />
                        <button onClick={click}> Set </button>
                    </div>
                </nav>

                <div id="table">
                    <table>
                        <thead>
                            <tr>
                                <th>Time</th><th>Day</th><th>Occation</th><th></th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>The Making</td>
                                <td>Today</td>
                                <td> Drama Drama Drama Drama Drama Drama Drama </td>
                                <td>D</td>
                            </tr>
                            <tr>
                                <td>Final Hour</td>
                                <td>Tommorrow</td>
                                <td>Thriller</td>
                                <td>D</td>
                            </tr>
                        </tbody>
                    </table>
                </div>

                <footer>Footer</footer>
            </div>
        </>
    );
}

export default Alarm;
