import { useState } from 'react';
import DateFnsUtils from '@date-io/date-fns'; // choose your lib
import {
    TimePicker,
    MuiPickersUtilsProvider,
} from '@material-ui/pickers';

import './Alarm.css'

const Alarm = () => {
    const [selectedDate, handleDateChange] = useState(new Date());
    const [alarmList, setAlarm] = useState([]);
    const [alarmCount, setAlarmCount] = useState(0);

    window.addEventListener('load', (event) => {
        console.log('page is fully loaded');
    });

    const click = () => {
        var alarm = {
            Time: { Hour: selectedDate.getHours(), Minute: selectedDate.getMinutes() },
            Day: "Today",
            Reminder: document.getElementById("reminder").value,
        };

        alarmList.unshift(alarm);
        setAlarm(alarmList);
        localStorage.setItem("AlarmList", JSON.stringify(alarmList));

        setAlarmCount(alarmCount + 1);
    }

    const showLocalStorage = () => {
        var list = JSON.parse(localStorage.getItem("AlarmList"));
        console.log(list[0]);
    }

    const showLocalStorageClear = () => {
        localStorage.removeItem("AlarmList");
    }


    const ArarmListShow = () => {
        if (alarmCount === 0) {
            return (
                <>
                    <table>
                        <thead>
                            <tr>
                                <th>Time</th><th>Day</th><th>Occation</th><th></th>
                            </tr>
                        </thead>
                    </table>
                </>
            );
        }
        else {
            return (
                <>
                    <table>
                        <thead>
                            <tr>
                                <th>Time</th><th>Day</th><th>Occation</th><th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {JSON.parse(localStorage.getItem("AlarmList")).map((item) => (
                                <tr>
                                    <td> {`${item.Time.Hour} : ${item.Time.Minute}`} </td>
                                    <td> {item.Day} </td>
                                    <td> {item.Reminder} </td>
                                    <td>D</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            );
        }
    }


    return (
        <>
            <div className="container">
                <nav>
                    <div>
                        <h2> Set Alarm </h2>
                    </div>
                    <div className="form">
                        <div className="timePicker">
                            <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                <TimePicker value={selectedDate} onChange={handleDateChange} />
                            </MuiPickersUtilsProvider>
                        </div>
                        <div className="reminderText">
                            <input type="textBox" id="reminder" placeholder="Reminder Text" />
                        </div>
                        <div className="setButton">
                            <button onClick={click}> Set </button>
                        </div>
                    </div>
                </nav>

                <div id="table">
                    <ArarmListShow />
                </div>

                <footer>
                    <button onClick={showLocalStorage} >LOCAL STORAGE</button>
                    <button onClick={showLocalStorageClear} >LOCAL STORAGE CLEAR</button>
                </footer>

            </div>
        </>
    );
}

export default Alarm;
