window.onload = function () {
    clock();
    setInterval(clock, 1000);
}

function clock() {
    const hourHand = document.getElementsByClassName('hourHand');
    const minuteHand = document.getElementsByClassName('minuteHand');
    const secondHand = document.getElementsByClassName('secondHand');
    const time = document.getElementsByClassName('time');

    const today = new Date();

    const second = today.getSeconds();
    const secondDeg = ((second / 60) * 360) + 360;
    secondHand.style.transform = `rotate(${secondDeg}deg)`;

    const minute = today.getMinutes();
    const minuteDeg = ((minute / 60) * 360);
    minuteHand.style.transform = `rotate(${minuteDeg}deg)`;

    const hour = today.getHours();
    const hourDeg = ((hour / 12) * 360);
    hourHand.style.transform = `rotate(${hourDeg}deg)`;

    // time.innerHTML = '<span>' + '<strong>' + hour + '</strong>' + ' : ' + minute + ' : ' + '<small>' + second + '</small>' + '</span>';

    time.innerHTML = "second";

}
